import React, { useState, useEffect } from "react";
import Header from "./Header";
import { Navigate, useParams } from "react-router-dom";
import axios from "axios";

const Dashboard = () => {
  const [checksession, setCheckSession] = useState(true);
  const [getdata, setGetData] = useState(true);
  const [idalumni, setIdalumni] = useState(0);

  //maklumat pelajar
  const [nama, setNama] = useState("");

  useEffect(() => {
    const getsessiondata = async () => {
      let sessiondata = sessionStorage.getItem("session");

      console.log(sessiondata);

      if (sessiondata != null) {
        // window.location.href = "/students";
        setIdalumni(sessiondata);
        console.log(`id alumni:  ${idalumni}`);
      } else {
        window.location.href = "/login";
      }

      setCheckSession(false);
    };
    if (checksession) getsessiondata();
  }, [checksession]);

  useEffect(() => {
    const fngetalumnidata = async () => {
      axios
        .post("https://alumniportal.ucyp.edu.my/api/getalumnidata", {
          idalumni: idalumni,
        })
        .then((response) => {
          if (response.data.status == 200) {
            sessionStorage.setItem("session", response.data.idalumnidata);
            let sessiondata = sessionStorage.getItem("session");
            console.log("session sekarang adalah: " + sessiondata);

            //   window.location.href =
            //     "/dashboard?idalumni=" + response.data.idalumnidata;
            setGetData(false);
          } else {
            alert("error fetching data");
          }
        });
    };
    if (getdata) fngetalumnidata();
  }, [getdata]);

  return (
    <div>
      <Header />
      <main id="main" class="main">
        <div class="pagetitle">
          <h1>Blank Page</h1>
          <nav>
            <ol class="breadcrumb">
              <li class="breadcrumb-item">
                <a href="index.html">Home</a>
              </li>
              <li class="breadcrumb-item">Pages</li>
              <li class="breadcrumb-item active">Blank</li>
            </ol>
          </nav>
        </div>

        <section class="section">
          <div class="row">
            <div class="col-lg-12">
              <div class="card">
                <div class="card-body">
                  <h5 class="card-title">My Profile</h5>
                  <br />
                  <label htmlFor="">Name: </label> &nbsp;
                  <input type="text" name="name" value={nama} />
                </div>
              </div>
            </div>
          </div>
        </section>
      </main>
    </div>
  );
};

export default Dashboard;
