import React, { useState, useEffect } from "react";
import Header from "./Header";
import { Navigate, useParams } from "react-router-dom";
import axios from "axios";

const Dashboard = () => {
  const [login, setLogin] = useState(0);
  const [dontredirect, setDontRedirect] = useState(false);
  const [goredirect, setGoRedirect] = useState(false);
  const [idd, setIdd] = useState(0);

  const [isSubmit, setIsSubmit] = useState(true);

  useEffect(() => {
    const authenticate = () => {
      let idalumni = new URLSearchParams(window.location.search).get(
        "idalumni"
      );
      setIsSubmit(false);

      if (idalumni != null) {
        // alert(idalumni);
        setLogin(1);
        setDontRedirect(true);
      } else {
        setGoRedirect(true);
      }
    };
    if (isSubmit) authenticate();
  }, [isSubmit]);

  useEffect(() => {
    const updateDontRedirect = () => {
      setDontRedirect(false);

      //   alert(login);

      //   if (login != 1) {
      //     // return <Navigate replace to="/login" />;
      //     // window.location.href = "/edit-student/1";
      //     window.location.href = "/login";
      //   }
    };
    if (dontredirect) updateDontRedirect();
  }, [dontredirect]);

  useEffect(() => {
    const updateGoRedirect = () => {
      //   window.location.href = "/login";
      <Navigate replace to={"/login"} />;
    };
    if (goredirect) updateGoRedirect();
  }, [goredirect]);

  const [checksession, setCheckSession] = useState(true);

  useEffect(() => {
    const getsessiondata = async () => {
      let sessiondata = sessionStorage.getItem("session");

      console.log(sessiondata);

      if (sessiondata != null) {
        // window.location.href = "/students";
        console.log(sessiondata);
      } else {
        window.location.href = "/login";
      }

      setCheckSession(false);
    };
    if (checksession) getsessiondata();
  }, [checksession]);

  return (
    <div>
      <Header />
      <main id="main" class="main">
        <div class="pagetitle">
          <h1>Blank Page</h1>
          <nav>
            <ol class="breadcrumb">
              <li class="breadcrumb-item">
                <a href="index.html">Home</a>
              </li>
              <li class="breadcrumb-item">Pages</li>
              <li class="breadcrumb-item active">Blank</li>
            </ol>
          </nav>
        </div>

        <section class="section">
          <div class="row">
            <div class="col-lg-12">
              <div class="card">
                <div class="card-body">
                  <h5 class="card-title">Example Card</h5>
                  <p>
                    This is an examle page with no contrnt. You can use it as a
                    starter for your custom pages.
                  </p>
                </div>
              </div>
            </div>
          </div>
        </section>
      </main>
    </div>
  );
};

export default Dashboard;
