import React, { useState, useEffect } from "react";
import Header from "./Header";
import { Navigate, useParams } from "react-router-dom";
import axios from "axios";

const Dashboard = () => {
  const [login, setLogin] = useState(false);
  const [idd, setIdd] = useState(0);

  const [isSubmit, setIsSubmit] = useState(false);

  let idalumni = new URLSearchParams(window.location.search).get("idalumni");

  if (idalumni > 0) {
    setLogin(true);
    setIdd(idalumni);
  }

  useEffect(() => {
    const authenticate = () => {
      let idalumni = new URLSearchParams(window.location.search).get(
        "idalumni"
      );

      if (idalumni < 1) {
        // return <Navigate replace to="/login" />;
        // window.location.href = "/edit-student/1";
        return <Navigate replace to="/login" />;
      }
    };
    if (isSubmit) authenticate();
  }, [isSubmit]);

  return (
    <div>
      <Header />
      <main id="main" class="main">
        <div class="pagetitle">
          <h1>Blank Page</h1>
          <nav>
            <ol class="breadcrumb">
              <li class="breadcrumb-item">
                <a href="index.html">Home</a>
              </li>
              <li class="breadcrumb-item">Pages</li>
              <li class="breadcrumb-item active">Blank</li>
            </ol>
          </nav>
        </div>

        <section class="section">
          <div class="row">
            <div class="col-lg-6">
              <div class="card">
                <div class="card-body">
                  <h5 class="card-title">Example Card</h5>
                  <p>
                    This is an examle page with no contrnt. You can use it as a
                    starter for your custom pages.
                  </p>
                </div>
              </div>
            </div>

            <div class="col-lg-6">
              <div class="card">
                <div class="card-body">
                  <h5 class="card-title">Example Card</h5>
                  <p>
                    This is an examle page with no contrnt. You can use it as a
                    starter for your custom pages.
                  </p>
                </div>
              </div>
            </div>
          </div>
        </section>
      </main>
    </div>
  );
};

export default Dashboard;
