import React, { Component, useState } from "react";
import axios from "axios";
import { Navigate, Link } from "react-router-dom";
import { useEffect } from "react/cjs/react.development";

const Login = () => {
  //   state = {
  //     username: "",
  //     password: "",
  //   };

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(`The name you entered was: ${name}`);
  };

  //   handleInput = (e) => {
  //     this.setState({
  //       [e.target.name]: e.target.value,
  //     });
  //   };

  //   saveStudent = async (e) => {
  //     e.preventDefault();

  //     const res = await axios.post(
  //       "https://alumniportal.ucyp.edu.my/api/login2",
  //       this.state
  //     );
  //     if (res.data.status == 200) {
  //       alert(res.data.session);

  //       localStorage.setItem("session", res.data.idalumnidata);
  //       let sessiondata = localStorage.getItem("session");
  //       console.log("session sekarang adalah: " + sessiondata);

  //       window.location.href = "/dashboard?idalumni=" + res.data.idalumnidata;
  //     } else {
  //       alert("oops");
  //     }
  //   };

  const [clicksubmit, setClicksubmit] = useState(false);

  useEffect(() => {
    e.preventDefault();
    const fnclick = (e) => {
      alert(username);
      setClicksubmit(false);
    };
    if (clicksubmit) fnclick();
  }, [clicksubmit]);

  return (
    <div>
      <main>
        <div class="container">
          <section class="section register min-vh-100 d-flex flex-column align-items-center justify-content-center py-4">
            <div class="container">
              <div class="row justify-content-center">
                <div class="col-lg-4 col-md-6 d-flex flex-column align-items-center justify-content-center">
                  <div class="d-flex justify-content-center py-4">
                    <div class="logo d-flex align-items-center w-auto">
                      <img src="assets/img/logo.png" alt="" />
                      <span class="d-none d-lg-block">Alumni Portal</span>
                    </div>
                  </div>

                  <div class="card mb-3">
                    <div class="card-body">
                      <div class="pt-4 pb-2">
                        <h5 class="card-title text-center pb-0 fs-4">Login.</h5>
                        <p class="text-center small">
                          Enter username and password to login.
                        </p>
                      </div>

                      <form
                        //   onSubmit={this.saveStudent}
                        class="row g-3 needs-validation"
                        novalidate
                      >
                        <div class="col-12">
                          <label for="yourUsername" class="form-label">
                            Username
                          </label>
                          <div class="input-group has-validation">
                            <span
                              class="input-group-text"
                              id="inputGroupPrepend"
                            >
                              <i className="fas fa-user"></i>
                            </span>
                            <input
                              type="text"
                              name="username"
                              class="form-control"
                              id="yourUsername"
                              value={username}
                              onChange={(e) => setUsername(e.target.value)}
                              required
                            />
                            <div class="invalid-feedback">
                              Please enter your username.
                            </div>
                          </div>
                        </div>

                        <div class="col-12">
                          <label for="yourPassword" class="form-label">
                            Password
                          </label>
                          <div class="input-group has-validation">
                            <span
                              class="input-group-text"
                              id="inputGroupPrepend"
                            >
                              <i className="fas fa-lock"></i>
                            </span>
                            <input
                              type="password"
                              name="password"
                              class="form-control"
                              id="yourPassword"
                              // onChange={this.handleInput}
                              // value={this.state.password}
                              value={password}
                              onChange={(password) => {
                                setPassword(password);
                              }}
                              required
                            />
                          </div>
                          <div class="invalid-feedback">
                            Please enter your password!
                          </div>
                        </div>
                        <div class="col-12">
                          <button
                            class="btn btn-primary w-100"
                            type="submit"
                            onClick={(e) => setClicksubmit(true)}
                          >
                            Login
                          </button>
                        </div>
                        <div class="col-12">
                          <p class="small mb-0">
                            Don't have account?
                            <Link to={"/students"}>asdasdasd</Link>
                          </p>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </section>
        </div>
      </main>
    </div>
  );
};

export default Login;
